import React from "react";
import "./modalReserva.css";

const ModalReserva = ({ showModal, closeModal }) => {
  const handleConfirmarReserva = () => {
    // Lógica para confirmar a reserva
    // Fechar o modal após a confirmação da reserva
    closeModal();
  };

  const renderModalContent = () => {
    return (
      <div className="modal">
        <div className="modal-content">
          <h2>Fazer Reserva</h2>
          {/* Campos para fazer reserva */}
          <div className="form-group">
            <label htmlFor="data">Data:</label>
            <input type="date" id="data" name="data" />
          </div>
          <div className="form-group">
            <label htmlFor="hora">Hora:</label>
            <input type="time" id="hora" name="hora" />
          </div>
          <div className="form-group">
            <label htmlFor="quadra">Quadra:</label>
            <select id="quadra" name="quadra">
              <option value="quadra-a">Quadra A</option>
              <option value="quadra-b">Quadra B</option>
              <option value="quadra-c">Quadra C</option>
            </select>
          </div>
          <button className="btn btn-primary" onClick={handleConfirmarReserva}>
            Confirmar Reserva
          </button>
        </div>
      </div>
    );
  };

  console.log("showModal:", showModal); // Adicione um console.log para rastrear a atualização de showModal

  return showModal ? renderModalContent() : null;
};

export default ModalReserva;
