import React from "react";
import { Button, Container, Table, Row, Col } from "react-bootstrap";

export default function TabelaCliente(props) {
  const { onTabela, editarUsuario, acaoBotao } = props;
  const cliente = props.dados;

  const excluirCliente = (cliente) => {
    fetch("http://localhost:4000/cliente", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(cliente),
    })
      .then((res) => res.json())
      .then((dados) => {
        alert(dados.messagem);
        onTabela(true);
      })
      .catch((erro) => {
        alert(erro.messagem);
      });
  };

  return (
    <Container>
      <Row className="mt-3 mb-3 border d-flex text-center">
        <h2>Lista de Clientes</h2>
      </Row>

      <Row>
        <Col>
          <Button
            onClick={() => {
              onTabela(false);
              acaoBotao("Cadastrar");
            }}
          >
            Cadastrar Cliente
          </Button>
        </Col>
      </Row>

      <Row className="mt-3 p-2 border">
        <Table striped bordered hover size="sm" className="text-center">
          <thead>
            <tr>
              <th>#</th>
              <th>Nome</th>
              <th>CPF</th>
              <th>Celular</th>
              <th>E-mail</th>
              <th>Senha</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {cliente.map((cliente) => (
              <tr key={cliente.cliente_id}>
                <td>{cliente.cliente_id}</td>
                <td>{cliente.cliente_nome}</td>
                <td>{cliente.cliente_cpf}</td>
                <td>{cliente.cliente_celular}</td>
                <td>{cliente.cliente_email}</td>
                <td>{cliente.cliente_senha}</td>
                <td>
                  <Button
                    onClick={() => {
                      editarUsuario(cliente);
                      onTabela(false);
                      acaoBotao("Editar");
                    }}
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                      fill="currentColor"
                      className="bi bi-pencil-square"
                      viewBox="0 0 16 16"
                    >
                      {/* ... (ícone de lápis) */}
                    </svg>
                  </Button>{" "}
                  <Button
                    onClick={() => {
                      if (window.confirm("Deseja excluir este cliente?")) {
                        excluirCliente(cliente);
                      }
                    }}
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="10"
                      height="16"
                      fill="currentColor"
                      className="bi bi-trash"
                      viewBox="0 0 16 16"
                    >
                      {/* ... (ícone de lixeira) */}
                    </svg>{" "}
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Row>
    </Container>
  );
}
