import React, { useState } from "react";
import "./reserva.css";

function Reserva() {
  // Estado para controlar a exibição da modal
  const [showModal, setShowModal] = useState(false);

  // Função para abrir a modal
  const openModal = () => {
    setShowModal(true);
  };

  // Função para fechar a modal
  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="reserva-container">
      <h1>Reservas</h1>
      <button onClick={openModal}>Fazer Reserva</button>

      {/* Tabela de reservas */}
      <table>
        <thead>
          <tr>
            <th>Data</th>
            <th>Horário</th>
            <th>Quadra</th>
          </tr>
        </thead>
        <tbody>
          {/* Linhas da tabela com dados de reserva */}
          {/* Você pode mapear os dados das reservas aqui */}
          <tr>
            <td>10/03/2024</td>
            <td>15:00 - 16:00</td>
            <td>Quadra A</td>
          </tr>
          {/* Adicione mais linhas conforme necessário */}
        </tbody>
      </table>

      {/* Modal para fazer reserva */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h2>Fazer Reserva</h2>
            {/* Conteúdo da modal para fazer reserva */}
          </div>
        </div>
      )}
    </div>
  );
}

export default Reserva;
