import { Button, Container, Table, Row, Col } from "react-bootstrap";
import React, { useState, useEffect } from "react";
//import prepararFormulario from "../Util/formsValidados";
export default function TabelaQuadra(props) {
  const [listaQuadra, setListaQuadra] = useState(props.dados);

  function excluirQuadra(quadra) {
    fetch(`http://localhost:4000/quadra/${quadra.id}`, {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((dados) => {
        alert(dados.mensagem);
        setListaQuadra(quadra);
        window.location.reload(true);
      })
      .catch((erro) => {
        alert(erro.mensagem);
      });
  }

  return (
    <Container>
      <Row className="mt-3 mb-3 border d-flex text-center">
        <h2>Lista de Quadras</h2>
      </Row>

      <Row>
        <Col>
          <Button
            onClick={() => {
              props.onTabela(false);
              props.editarAtualizando(false);
              props.acaoBotao("Cadastrar");
            }}
          >
            Cadastrar Quadra
          </Button>
        </Col>
      </Row>

      <Row className="mt-3 p-2 border">
        <Table striped bordered hover size="sm" className="text-center">
          <thead>
            <tr>
              <th>#</th>
              <th>Tipo</th>
              <th>Localização</th>
              <th>Preço</th>
              <th>Horário Reservado</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {listaQuadra.map((quadra) => (
              <tr key={quadra.id}>
                <td>{quadra.id}</td>
                <td>{quadra.tipo}</td>
                <td>{quadra.localizacao}</td>
                <td>{quadra.preco}</td>
                <td>{quadra.hora ? "Sim" : "Não"}</td>
                <td>
                  <Button
                    onClick={() => {
                      props.editarQuadra(quadra);
                      props.onTabela(false);
                      props.acaoBotao("Editar");
                    }}
                  >
                    Editar
                  </Button>{" "}
                  <Button
                    onClick={() => {
                      if (window.confirm("Deseja excluir esta quadra?")) {
                        excluirQuadra(quadra);
                      }
                    }}
                  >
                    Excluir
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Row>
    </Container>
  );
}
