import React, { useEffect, useRef, useState } from "react";
import { Container } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Row from "react-bootstrap/Row";
import STATUS from "../Util/util";
import TelaCarregamento from "../Screen/TelaCarregamento";
import TelaErro from "../Screen/TelaErro";

export default function FormCadUsuario(props) {
  const [formValidado, setFormValidado] = useState(false);
  const [status, setStatus] = useState(STATUS.sucesso);
  const [usuario, setUsuario] = useState(props.usuarios);
  const [emEdicao, setEmEdicao] = useState(props.atualizando);

  const nome = useRef(usuario.usuario_nome);
  const cpf = useRef(usuario.usuario_cpf);
  const email = useRef(usuario.usuario_email);
  const senha = useRef(usuario.usuario_senhas);

  useEffect(() => {
    //console.log(props.usuarios);
    if (props.usuarios) {
      setUsuario(props.usuarios);
      nome.current.value = usuario.usuario_nome;
      cpf.current.value = usuario.usuario_cpf;
      email.current.value = usuario.usuario_email;
      senha.current.value = usuario.usuario_senha;
    }
    //console.log(usuario);
  }, [props.usuarios]);

  function manipularSubmissao(evento) {
    const formulario = evento.currentTarget;
    if (formulario.checkValidity()) {
      const usuario = validarDados();
      if (usuario) {
        setStatus(STATUS.ocioso);
        cadastrarUsuario(usuario);
      }
    }
    evento.preventDefault();
    evento.stopPropagation();
    setFormValidado(true);
  }

  function cadastrarUsuario(usuario) {
    fetch("http://localhost:4000/usuario", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(usuario),
    })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setStatus(STATUS.sucesso);
        alert(dados.mensagem);
        setUsuario(usuario);
        props.onTabela(true);
      })
      .catch((erro) => {
        //console.log(`Entrou no erro ${JSON.stringify(erro)} `);
        setStatus(STATUS.erro);
      });
  }

  /*function alterarUsuario(usuario) {
    fetch("http://localhost:4000/usuario", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(usuario),
    })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setStatus(STATUS.sucesso);
        alert(dados.mensagem);
        props.usuarios.push(usuario);
        props.onTabela(true);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
      });
  }*/

  // console.log(`aqui é antes do validar dados ${nome.current.value}` );

  // const _usuario = useRef(usuario)

  function validarDados() {
    const _usuario = {
      nome: nome.current.value,
      cpf: cpf.current.value,
      email: email.current.value,
      senha: senha.current.value,
    };

    console.log(`aqui é o validar dados ${_usuario.nome}`);
    if (_usuario.nome && _usuario.cpf && _usuario.email && _usuario.senha)
      return _usuario;
    else {
      return undefined;
    }
  }

  if (status === STATUS.sucesso) {
    return (
      <Container>
        <Row className="mt-3 mb-3 border d-flex text-center">
          <h2>Formulario de Cadastro de Usuario</h2>
        </Row>
        <Row className="mt-3 p-2 border">
          <Form
            noValidate
            validated={formValidado}
            onSubmit={manipularSubmissao}
          >
            <Row className="mb-3">
              <Form.Group as={Col} md="3">
                <Form.Label>Nome</Form.Label>
                <Form.Control
                  id="nome"
                  name="nome"
                  required
                  type="text"
                  ref={nome}
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o nome do Usuario
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>C.P.F</Form.Label>
                <Form.Control
                  id="cpf"
                  name="cpf"
                  required
                  type="text"
                  ref={cpf}
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o C.P.F do Usuario
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>Email</Form.Label>
                <InputGroup hasValidation>
                  <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                  <Form.Control
                    id="email"
                    name="email"
                    type="email"
                    aria-describedby="inputGroupPrepend"
                    required
                    ref={email}
                  />
                  <Form.Control.Feedback type="invalid">
                    Por favor, Informa o E-mail do Usuario
                  </Form.Control.Feedback>
                </InputGroup>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>Senha</Form.Label>
                <Form.Control
                  id="senha"
                  name="senha"
                  required
                  type="password"
                  ref={senha}
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o Senha do Usuario
                </Form.Control.Feedback>
              </Form.Group>
            </Row>
            <Button type="submit" id="cadastrar" variant="success">
              Cadastrar
            </Button>{" "}
            <Button type="submit" id="atualizar" disabled variant="warning">
              Atualizar
            </Button>{" "}
            <Button type="submit" id="excluir" disabled variant="danger">
              Excluir
            </Button>{" "}
            <Button type="submit" id="limpar" variant="secondary">
              Limpar
            </Button>{" "}
            <Button
              id="limpar"
              onClick={() => {
                props.onTabela(true);
              }}
              type="submit"
              variant="outline-primary"
            >
              Voltar
            </Button>
          </Form>
        </Row>
      </Container>
    );
  } else if (status === STATUS.ocioso) {
    return <TelaCarregamento />;
  } else {
    return <TelaErro mensagem="Não foi possivel Gravar o Cliente" />;
  }
}
