import React, { useEffect, useRef, useState } from "react";
import { Container } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Row from "react-bootstrap/Row";
import STATUS from "../Util/util";
import TelaCarregamento from "../Screen/TelaCarregamento";
import TelaErro from "../Screen/TelaErro";
import InputMask from "react-input-mask";

export default function FormCadQuadra(props) {
  const [formValidado, setFormValidado] = useState(false);
  const [status, setStatus] = useState(STATUS.sucesso);
  const [quadra, setQuadra] = useState(props.quadra);
  const [acao, setAcao] = useState(props.botaoAcao);

  const tipo = useRef(quadra.tipo);
  const localizacao = useRef(quadra.localizacao);
  const preco = useRef(quadra.preco);
  const horarioReservado = useRef(quadra.horarioReservado);
  const id = useRef(quadra.id);

  useEffect(() => {
    if (props.atualizando) {
      if (props.quadra) {
        setQuadra(props.quadra);
        tipo.current.value = quadra.tipo;
        localizacao.current.value = quadra.localizacao;
        preco.current.value = quadra.preco;
        horarioReservado.current.checked = quadra.horarioReservado;
        id.current.value = quadra.id;
      }
    } else {
      tipo.current.value = "";
      localizacao.current.value = "";
      preco.current.value = "";
      horarioReservado.current.checked = false;
    }
  }, [props.quadra]);

  function manipularSubmissao(evento) {
    const formulario = evento.currentTarget;
    if (formulario.checkValidity()) {
      const quadra = validarDados();
      if (quadra && !props.atualizando) {
        setStatus(STATUS.ocioso);
        cadastrarQuadra(quadra);
      } else {
        setStatus(STATUS.ocioso);
        alterarQuadra(quadra);
      }
    }
    evento.preventDefault();
    evento.stopPropagation();
    setFormValidado(true);
  }

  function cadastrarQuadra(quadra) {
    fetch("http://localhost:4000/quadra", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(quadra),
    })
      .then((res) => res.json())
      .then((dados) => {
        setStatus(STATUS.sucesso);
        alert(dados.messagem);
        setQuadra(quadra);
        props.onTabela(true);
        window.location.reload(true);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
        console.log(erro.message);
      });
  }

  function alterarQuadra(quadra) {
    fetch("http://localhost:4000/quadra", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(quadra),
    })
      .then((res) => res.json())
      .then((dados) => {
        setStatus(STATUS.sucesso);
        alert(dados.messagem);
        setQuadra(quadra);
        window.location.reload(true);
        props.onTabela(true);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
        console.log(erro.message);
      });
  }

  function validarDados() {
    const quadra = {
      tipo: tipo.current.value,
      localizacao: localizacao.current.value,
      preco: preco.current.value,
      horarioReservado: horarioReservado.current.checked,
      id: id.current.value,
    };

    if (quadra.tipo && quadra.localizacao && quadra.preco) {
      return quadra;
    } else {
      ///return undefined;
    }
  }

  if (status === STATUS.sucesso) {
    return (
      <Container>
        <Row className="mt-3 mb-3 border d-flex  align-content-center">
          <h2>Formulário de Cadastro de Quadra</h2>
        </Row>
        <Row className="mt-3 p-2 border ">
          <Form
            noValidate
            validated={formValidado}
            onSubmit={manipularSubmissao}
          >
            <Row className="mb-3 p-2  justify-content-center align-content-center">
              <Row className="mb-3">
                <Col>
                  <Form.Group as={Col} md="1">
                    <Form.Label hidden={acao === "Cadastrar" ? true : false}>
                      Id
                    </Form.Label>
                    <Form.Control
                      hidden={acao === "Cadastrar" ? true : false}
                      id="id"
                      name="id"
                      required
                      type="text"
                      ref={id}
                      disabled
                    />
                    <Form.Control.Feedback type="invalid">
                      Por favor, informe o ID da Quadra
                    </Form.Control.Feedback>
                  </Form.Group>
                </Col>
              </Row>
              <Form.Group as={Col} md="4">
                <Form.Label>Tipo</Form.Label>
                <Form.Control id="tipo" name="tipo" required type="text" ref={tipo} />
                <Form.Control.Feedback type="invalid">
                  Por favor, informe o tipo da Quadra
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="4">
                <Form.Label>Localização</Form.Label>
                <Form.Control
                  id="localizacao"
                  name="localizacao"
                  required
                  type="text"
                  ref={localizacao}
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, informe a localização da Quadra
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="4">
                <Form.Label>Preço</Form.Label>
                <Form.Control
                  id="preco"
                  name="preco"
                  placeholder="Digite o preço"
                  required
                  type="number"
                  ref={preco}
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, informe o preço da Quadra
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="">
                <Form.Check
                  type="checkbox"
                  id="horarioReservado"
                  name="horarioReservado"
                  label="Horário Reservado"
                  ref={horarioReservado}
                />
              </Form.Group>
            </Row>
            <Button
              type="submit"
              disabled={acao === "Cadastrar" ? false : true}
              variant="success"
            >
              Cadastrar
            </Button>{" "}
            <Button
              type="submit"
              disabled={acao === "Editar" ? false : true}
              variant="warning"
            >
              Atualizar
            </Button>{" "}
            <Button type="submit" name="limpar" variant="secondary">
              Limpar
            </Button>{" "}
            <Button
              id="voltar"
              onClick={() => {
                props.onTabela(true);
              }}
              type="submit"
              variant="outline-primary"
            >
              Voltar
            </Button>
          </Form>
        </Row>
      </Container>
    );
  } else if (status === STATUS.ocioso) {
    return <TelaCarregamento />;
  } else {
    return <TelaErro mensagem="Não foi possível gravar a Quadra" />;
  }
}
