import React, { useState } from "react";
import "./modalReserva.css";
import QuadrasList from "./quadraList";
import api from "./api";
import authService from "./authService";

const ModalReserva = ({ showModal, closeModal, handleSubmit }) => {
  const [date, setDate] = useState("");
  const [horaInicio, setHoraInicio] = useState("");
  const [horaFim, setHoraFim] = useState("");
  const [quadraId, setQuadraId] = useState("");
  const [disponibilidade, setDisponibilidade] = useState(false); // Estado para controlar a disponibilidade da reserva

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  const handleTimeChange = (e) => {
    setTime(e.target.value);
  };

  // Função para verificar a disponibilidade da reserva
  const handleVerificarDisponibilidade = async () => {
    try {
      const token = authService.getToken();
      const response = await api.get(
        "reservadisponivel",
        { data, horaInicio, horaFim, quadraId },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Adicione o token JWT no cabeçalho Authorization
            "Content-Type": "application/json", // Especifique o tipo de conteúdo da solicitação
          },
        }
      );
      //setQuadras(response.data);
    } catch (error) {
      console.error("Erro ao obter dados das quadras:", error);
    }
    setDisponibilidade(true); // Exemplo: supondo que a reserva está disponível
  };

  return (
    showModal && (
      <div className="modal-container">
        <div className="modal-card">
          <div className="card-header">
            <h2 className="card-title">Cadastro de Reserva </h2>
          </div>
          <div className="card-body">
            <form onSubmit={(e) => handleSubmit(e, date, time)}>
              <QuadrasList />
              <div className="form-group">
                <label htmlFor="date">Data:</label>
                <input
                  type="date"
                  id="date"
                  value={date}
                  onChange={handleDateChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="time">Hora Inicio:</label>
                <input
                  type="time"
                  id="time"
                  value={horaInicio}
                  onChange={handleTimeChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="time">Hora Fim:</label>
                <input
                  type="time"
                  id="time"
                  value={horaFim}
                  onChange={handleTimeChange}
                  required
                />
              </div>

              {/* Botão para verificar disponibilidade */}
              <button
                className="btn btn-secondary mb-4 mr-2"
                onClick={handleVerificarDisponibilidade}
              >
                Verificar Disponibilidade
              </button>

              {/* Botão para reservar (desabilitado se a reserva não estiver disponível) */}
              <button
                className="btn btn-primary mb-4"
                type="submit"
                disabled={!disponibilidade} // Desabilita o botão se a reserva não estiver disponível
              >
                Reservar
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalReserva;
