import React, { useRef, useState } from "react";
import { Container } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import STATUS from "../Util/util";
import TelaCarregamento from "../Screen/TelaCarregamento";
import TelaErro from "../Screen/TelaErro";

export default function FormCadReserva(props) {
  function cadastarReserva(reserva) {
    fetch("http://localhost:4000/reserva", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(reserva),
    })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setStatus(STATUS.sucesso);
        alert(dados.mensagem);
        props.listareservas.push(reserva);
        props.onTabela(true);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
      });
  }

  /*function alterarReserva(reserva) {
    fetch("http://localhost:4000/reserva", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(reserva),
    })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setStatus(STATUS.sucesso);
        alert(dados.mensagem);
        props.listareservas.push(reserva);
        props.onTabela(true);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
      });
  }*/

  const [formValidado, setFormValidado] = useState(false);
  const [status, setStatus] = useState(STATUS.sucesso);
  const [reservas, setReservas] = (listaReservas);

  const data = useRef("");
  const hora = useRef("");

  function validarDados() {
    const reserva = {
      nome: data.current.value,
      cpf: hora.current.value,
    };

    //

    if (reserva.data && reserva.hora) return reserva;
    else {
      return undefined;
    }
  }

  function manipularSubmissao(evento) {
    const formulario = evento.currentTarget;
    if (formulario.checkValidity()) {
      const reserva = validarDados();
      if (reserva) {
        setStatus(STATUS.ocioso);
        cadastarReserva(reserva);
      }
    }
    evento.preventDefault();
    evento.stopPropagation();
    setFormValidado(true);
  }

  if (status === STATUS.sucesso) {
    return (
      <Container>
        <Row className="mt-3 mb-3 border d-flex text-center">
          <h2>Formulario de Reservaaa</h2>
        </Row>
        <Row className="mt-3 p-2 border">
          <Form
            noValidate
            validated={formValidado}
            onSubmit={manipularSubmissao}
          >
            <Row className="mb-3">
              <Form.Group as={Col} md="3">
                <Form.Label>Data</Form.Label>
                <Form.Control
                  id="data"
                  name="data"
                  required
                  type="date"
                  placeholder="Data"
                  ref={data}
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o Data da reserva
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>Hora</Form.Label>
                <Form.Select
                  aria-label="Default select example"
                  id="hora"
                  name="hora"
                  required
                  ref={hora}
                >
                  <option>Selecione...</option>
                  <option value="0">00:00</option>
                  <option value="1">01:00</option>
                  <option value="2">02:00</option>
                  <option value="3">03:00</option>
                  <option value="4">04:00</option>
                  <option value="5">05:00</option>
                  <option value="6">06:00</option>
                  <option value="7">07:00</option>
                  <option value="8">08:00</option>
                  <option value="9">09:00</option>
                  <option value="10">10:00</option>
                  <option value="11">11:00</option>
                  <option value="12">12:00</option>
                  <option value="13">13:00</option>
                  <option value="14">14:00</option>
                  <option value="15">15:00</option>
                  <option value="16">16:00</option>
                  <option value="17">17:00</option>
                  <option value="18">18:00</option>
                  <option value="19">19:00</option>
                  <option value="20">20:00</option>
                  <option value="21">21:00</option>
                  <option value="22">22:00</option>
                  <option value="23">23:00</option>
                </Form.Select>

                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o C.P.F do reserva
                </Form.Control.Feedback>
              </Form.Group>
            </Row>
            <Button type="submit" variant="success">
              Cadastrar
            </Button>{" "}
            <Button type="submit" variant="danger">
              Limpar
            </Button>{" "}
            <Button
              onClick={() => {
                props.onTabela(true);
              }}
              type="submit"
              variant="outline-primary"
            >
              Voltar
            </Button>
          </Form>
        </Row>
      </Container>
    );
  } else if (status === STATUS.ocioso) {
    return <TelaCarregamento />;
  } else {
    return <TelaErro mensagem="Não foi possivel Gravar o Cliente" />;
  }
}
