import React, { useState } from "react";
import "./modalReserva.css";

const ModalReserva = ({ showModal, closeModal, handleSubmit }) => {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [court, setCourt] = useState("");

  const [availableCourts, setAvailableCourts] = useState([]);

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  const handleTimeChange = (e) => {
    setTime(e.target.value);
  };

  const handleCourtChange = (e) => {
    setCourt(e.target.value);
  };

  const listAvailableCourts = async () => {
    try {
      // Fazer a solicitação HTTP para buscar as quadras disponíveis
      const response = await axios.get("URL_DO_SEU_ENDPOINT");
      
      // Verificar se a solicitação foi bem-sucedida e definir as quadras disponíveis no estado
      if (response.status === 200) {
        const availableCourtsData = response.data; // Supondo que os dados das quadras estejam no formato de array
        setAvailableCourts(availableCourtsData);
      } else {
        // Tratar casos de erro, se necessário
      }
    } catch (error) {
      // Tratar erros de solicitação, se necessário
      console.error("Erro ao buscar quadras disponíveis:", error);
    }
  };

  // Obtém a data atual no formato YYYY-MM-DD
  const currentDate = new Date().toISOString().split("T")[0];

  return (
    showModal && (
      <div className="modal-container">
        <div className="modal-card">
          <div className="card-header">
            <h2 className="card-title">Cadastro de Reserva </h2>
            <button className="close-button" onClick={closeModal}>
              {/* Ícone do botão de fechar */}
            </button>
          </div>
          <div className="card-body">
            <form onSubmit={(e) => handleSubmit(e, date, time, court)}>
              <div className="form-group">{/* Campo de data */}</div>
              <div className="form-group">{/* Campo de hora */}</div>
              <div className="form-group">
                {/* Campo de seleção de quadra */}
              </div>
              <button
                className="btn btn-primary mb-4"
                type="button"
                onClick={listAvailableCourts}
              >
                Fazer Reserva
              </button>
              <div className="court-cards-container">
                {availableCourts.map((court, index) => (
                  <div className="court-card" key={index}>
                    <h3>{court.name}</h3>
                    <p>{court.description}</p>
                    {/* Outras informações sobre a quadra, se necessário */}
                    <button onClick={() => handleReserve(court)}>
                      Reservar
                    </button>
                  </div>
                ))}
              </div>
            </form>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalReserva;
