import Pagina from "../templates/Pagina";
import FormCadQuadra from "../Forms/FormCadQuadra";
import TabelaQuadra from "../Tables/TabelaDeQuadra";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Util/util";

export default function TelaCadastroDeQuadra(props) {
  function preparaAcao(acao) {
    setAcao(acao);
  }

  function preparaQuadraExcluir(quadra) {
    setExcluirQuadra(quadra);
  }

  function prepararQuadraEdicao(quadra) {
    setAtualizando(true);
    setQuadraEmEdicao(quadra);
  }

  function buscarQuadras() {
    fetch("http://localhost:4000/quadra", { method: "GET" })
      .then((res) => res.json())
      .then((dados) => {
        setListaQuadra(dados);
        setStatus(STATUS.sucesso);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
        console.log(erro.message);
      });
  }

  const [status, setStatus] = useState(STATUS.sucesso);
  const [listaQuadra, setListaQuadra] = useState([]);
  const [atualizando, setAtualizando] = useState(false);
  const [exibeTabela, setExibirTabela] = useState(true);
  const [quadraEmEdicao, setQuadraEmEdicao] = useState({
    id: 0,
    tipo: "",
    localizacao: "",
    preco: 0,
    horarioReservado: false,
  });

  const [excluirQuadra, setExcluirQuadra] = useState({});
  const [acao, setAcao] = useState("");

  useEffect(() => {
    setStatus(STATUS.ocioso);
    buscarQuadras();
  }, []);

  if (exibeTabela) {
    if (status === STATUS.ocioso) {
      return <TelaCarregamento />;
    } else if (status === STATUS.sucesso) {
      return (
        <Pagina>
          <TabelaQuadra
            dados={listaQuadra}
            onTabela={setExibirTabela}
            editarQuadra={prepararQuadraEdicao}
            editarAtualizando={setAtualizando}
            dadosExcluir={preparaQuadraExcluir}
            acaoEditar={setAcao}
            acaoBotao={preparaAcao}
          />
        </Pagina>
      );
    } else {
      return (
        <TelaErro mensagem="Não foi possível recuperar os dados das Quadras"></TelaErro>
      );
    }
  } else {
    return (
      <Pagina>
        <FormCadQuadra
          quadra={quadraEmEdicao}
          excluir={excluirQuadra}
          listaQuadra={listaQuadra}
          atualizando={atualizando}
          onTabela={setExibirTabela}
          botaoAcao={acao}
        />
      </Pagina>
    );
  }
}
