import React, { useState, useEffect } from "react";
import Pagina from "../templates/Pagina";
import FormCadcliente from "../Forms/FormCadcliente";
import Tabelacliente from "../Tables/TabelaDecliente";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Util/util";

export default function TelaCadastroDecliente(props) {
  const [status, setStatus] = useState(STATUS.sucesso);
  const [listacliente, setListacliente] = useState([]);
  const [atualizando, setAtualizando] = useState(false);
  const [exibeTabela, setExibirTabela] = useState(true);
  const [clienteEmEdicao, setclienteEmEdicao] = useState({
    id: 0,
    name: "",
    cpf: "",
    celular: "",
    email: "",
    senha: "",
  });
  const [excluircliente, setExcluircliente] = useState({});
  const [acao, setAcao] = useState("");

  useEffect(() => {
    async function fetchData() {
      try {
        setStatus(STATUS.ocioso);
        const response = await fetch("http://localhost:4000/cliente", {
          method: "GET",
        });
        const dados = await response.json();
        setListacliente(dados);
        setStatus(STATUS.sucesso);
      } catch (erro) {
        setStatus(STATUS.erro);
        console.error(erro.message);
      }
    }

    fetchData();
  }, []);

  function prepararAcao(acao) {
    setAcao(acao);
  }

  function prepararclienteExcluir(cliente) {
    setExcluircliente(cliente);
  }

  function prepararclienteEdicao(cliente) {
    setAtualizando(true);
    setclienteEmEdicao(cliente);
  }

  return exibeTabela ? renderizarTabela() : renderizarFormulario();

  function renderizarTabela() {
    switch (status) {
      case STATUS.ocioso:
        return <TelaCarregamento />;
      case STATUS.sucesso:
        return (
          <Pagina>
            <Tabelacliente
              dados={listacliente}
              onTabela={setExibirTabela}
              editarcliente={prepararclienteEdicao}
              editarAtualizando={setAtualizando}
              dadosExcluir={prepararclienteExcluir}
              acaoEditar={setAcao}
              acaoBotao={prepararAcao}
            />
          </Pagina>
        );
      case STATUS.erro:
        return (
          <TelaErro messagem="Não foi possível recuperar os dados dos Clientes" />
        );
      default:
        return null;
    }
  }

  function renderizarFormulario() {
    return (
      <Pagina>
        <FormCadcliente
          clientes={clienteEmEdicao}
          exclui={excluircliente}
          listacliente={listacliente}
          atualizando={atualizando}
          onTabela={setExibirTabela}
          botAcao={acao}
        />
      </Pagina>
    );
  }
}
