// ModalReserva.js

import React, { useState } from "react";
import "./modalReserva.css";
import QuadrasList from "./quadraList";
import api from "./api";
import authService from "./authService";

const ModalReserva = ({ showModal, closeModal, handleSubmit }) => {
  const [data, setData] = useState("");
  const [horaInicio, setHoraInicio] = useState("");
  const [horaFim, setHoraFim] = useState("");
  const [quadraId, setQuadraId] = useState("");
  const [disponibilidade, setDisponibilidade] = useState(false);
  const [mensagemResposta, setMensagemResposta] = useState("");

  const handleDateChange = (e) => {
    setData(e.target.value);
  };

  const handleHoraInicioChange = (e) => {
    setHoraInicio(e.target.value);
  };

  const handleHoraFimChange = (e) => {
    setHoraFim(e.target.value);
  };

  const handleQuadraChange = (selectedQuadraId) => {
    // Função para atualizar quadraId
    setQuadraId(selectedQuadraId);
  };

  const handleVerificarDisponibilidade = async (e) => {
    e.preventDefault();
    if ((!data, !horaInicio, !horaFim, !quadraId)) {
      setErrorMessage("Por favor, preencha os campos");
      return;
    }
    try {
      const token = authService.getToken();
      const response = await api.get("/reserva/reservadisponivel", {
        data,
        horaInicio,
        horaFim,
        quadraId,
      });
      setMensagemResposta(response.data.message);
    } catch (error) {
      console.error("Erro ao verificar disponibilidade:", error);
      setMensagemResposta("Erro ao verificar disponibilidade");
    }
    setDisponibilidade(true);
  };

  return (
    showModal && (
      <div className="modal-container">
        <div className="modal-card">
          <div className="card-header">
            <h2 className="card-title">Cadastro de Reserva</h2>
          </div>
          <div className="card-body">
            <form>
              <QuadrasList onChange={handleQuadraChange} />{" "}
              {/* Passa a função onChange */}
              <div className="form-group">
                <label htmlFor="data">Data:</label>
                <input
                  type="date"
                  id="data"
                  value={data}
                  onChange={handleDateChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="horaInicio">Hora Inicio:</label>
                <input
                  type="time"
                  id="horaInicio"
                  value={horaInicio}
                  onChange={handleHoraInicioChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="horaFim">Hora Fim:</label>
                <input
                  type="time"
                  id="horaFim"
                  value={horaFim}
                  onChange={handleHoraFimChange}
                  required
                />
              </div>
              <button
                className="btn btn-secondary mb-4 mr-2"
                onClick={handleVerificarDisponibilidade}
              >
                Verificar Disponibilidade
              </button>
              <button
                className="btn btn-primary mb-4"
                onClick={(e) => handleSubmit(e)}
                disabled={!disponibilidade}
              >
                Reservar
              </button>
              <div className="resposta-message">{mensagemResposta}</div>
            </form>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalReserva;
