import React, { useState, useEffect } from "react";
import "./reserva.css";
import Menu from "./menu";
import ModalReserva from "./modalReserva";
import api from "./api";

function Reserva() {
  // Estado para controlar a exibição da modal
  const [showModal, setShowModal] = useState(false);
  // Estado para armazenar as informações do cliente e suas reservas
  const [clienteReservas, setClienteReservas] = useState([]);

  console.log(clienteReservas);

  // Função para abrir a modal
  const openModal = () => {
    setShowModal(true);
  };

  // Função para fechar a modal
  const closeModal = () => {
    setShowModal(false);
  };

  // Função para carregar as informações do cliente e suas reservas
  const fetchData = async () => {
    try {
      // Faça uma solicitação para obter as informações do cliente e suas reservas
      const response = await api.get("/reserva", {
        headers: {
          Authorization: `Basic ${token}`,
        },
      });
      console.log("response.data", response);
      setClienteReservas(response.data);
    } catch (error) {
      console.error("Erro ao obter dados do cliente e suas reservas:", error);
    }
  };

  // Use o useEffect para chamar fetchData quando o componente for montado
  useEffect(() => {
    fetchData();
  }, []);

  // Função para editar uma reserva
  const editarReserva = (id) => {
    // Lógica para editar a reserva com o ID fornecido
    console.log("Editar reserva com ID:", id);
  };

  // Função para excluir uma reserva
  const excluirReserva = (id) => {
    // Lógica para excluir a reserva com o ID fornecido
    console.log("Excluir reserva com ID:", id);
  };

  return (
    <div>
      <Menu />
      <div className="container mt-5">
        <h1 className="mb-4 text-center">Reservas</h1>
        <button onClick={openModal} className="btn btn-primary mb-4">
          Fazer Reserva
        </button>
        {/* Tabela de reservas */}
        <table className="table mx-auto">
          <thead>
            <tr>
              <th scope="col" className="text-center">
                Data
              </th>
              <th scope="col" className="text-center">
                Horário
              </th>
              <th scope="col" className="text-center">
                Quadra
              </th>
              <th scope="col" className="text-center">
                Ações
              </th>
            </tr>
          </thead>
          <tbody>
            {/* Mapeie as reservas do cliente */}
            {clienteReservas.map((reserva) => (
              <tr key={reserva.id}>
                <td className="text-center">{reserva.data}</td>
                <td className="text-center">{reserva.horario}</td>
                <td className="text-center">{}</td>
                <td className="text-center">
                  {/* Botões de ação para editar e excluir */}
                  <button
                    className="btn btn-primary mr-2"
                    onClick={() => editarReserva(1)}
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                      fill="currentColor"
                      class="bi bi-pencil-square"
                      viewBox="0 0 16 16"
                    >
                      <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                      <path
                        fill-rule="evenodd"
                        d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5z"
                      />
                    </svg>
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => excluirReserva(1)}
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                      fill="currentColor"
                      class="bi bi-trash3-fill"
                      viewBox="0 0 16 16"
                    >
                      <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5m-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5M4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06m6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528M8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5" />
                    </svg>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <ModalReserva showModal={showModal} closeModal={closeModal} />
      </div>
    </div>
  );
}

export default Reserva;
