import Pagina from "../templates/Pagina";
import FormCadUsuario from "../Forms/FormCadUsuario";
import TabelaUsuario from "../Tables/TabelaDeUsuario";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Util/util";

export default function TelaCadastroDeUsuario(props) {
  function buscarUsuario() {
    fetch("http://localhost:4000/usuario", { method: "GET" })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setListaUsuarios(dados);
        setStatus(STATUS.sucesso);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
        console.log(erro.mensagem)
      });
  }

  useEffect(() => {
    setStatus(STATUS.ocioso);
    buscarUsuario();
  }, [listaUsuarios]);

  
  const [exibitTabela, setExibirTabela] = useState(true);
  const [status, setStatus] = useState(STATUS.sucesso);
  const [listaUsuarios, setListaUsuarios] = useState([]);

  if (exibitTabela) {
    if (status === STATUS.sucesso) {
      return <TelaCarregamento />;
    } else if (status === STATUS.carregado) {
      return (
        <Pagina>
          <TabelaUsuario dados={listaUsuarios} onTabela={setExibirTabela} />
        </Pagina>
      );
    } else {
      return (
        <TelaErro mensagem="Não foi possivel recuperar os dados dos Usuarios"></TelaErro>
      );
    }
  } else {
    return (
      <Pagina>
        <FormCadUsuario
          listaUsuarios={listaUsuarios}
          onTabela={setExibirTabela}
        />
      </Pagina>
    );
  }
}
