import { Button, Container, Table, Row, Col } from "react-bootstrap";
//import prepararFormulario from "../Util/formsValidados";

export default function TabelaUsuario(props) {
  function prepararFormulario(
    id = "",
    nome = "",
    cpf = "",
    email = "",
    senha = "",
    acao = ""
  ) {
    let botaoCadastrar = document.getElementById("cadastrar");
    let botaoAtualizar = document.getElementById("atualizar");
    let botaoExcluir = document.getElementById("excluir");

    document.getElementById("id").value = id;
    document.getElementById("nome").value = nome;
    document.getElementById("cpf").value = cpf;
    document.getElementById("email").value = email;
    document.getElementById("senha").value = senha;

    if (acao === "exclusao") {
      botaoCadastrar.disabled = true;
      botaoAtualizar.disabled = true;
      botaoExcluir.disabled = false;
    } else if (acao === "atualizacao") {
      botaoCadastrar.disabled = true;
      botaoAtualizar.disabled = false;
      botaoExcluir.disabled = true;
    }
  }
  return (
    <Container>
      <Row className="mt-3 mb-3 border d-flex text-center">
        <h2>Lista de Usuários</h2>
      </Row>

      <Row>
        <Col>
          <Button
            onClick={() => {
              props.onTabela(false);
            }}
          >
            Cadastrar Usuario
          </Button>
        </Col>
      </Row>

      <Row className="mt-3 p-2 border">
        <Table striped bordered hover size="sm" className="text-center">
          <thead>
            <tr>
              <th>#</th>
              <th>Nome</th>
              <th>CPF</th>
              <th>E-mail</th>
              <th>senha</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {props.dados.map((usuario) => {
              return (
                <tr key={usuario.usuario_id}>
                  <td>{usuario.id}</td>
                  <td>{usuario.usuario_nome}</td>
                  <td>{usuario.usuario_cpf}</td>
                  <td>{usuario.usuario_email}</td>
                  <td>{usuario.usuario_senha}</td>
                  <td>
                    <Button
                      onClick={
                        (prepararFormulario(
                          usuario.usuario_id,
                          usuario.usuario_nome,
                          usuario.usuario_cpf,
                          usuario.usuario_email,
                          usuario.usuario_senha,
                          "atualizacao"
                        ),
                        () => {
                          props.onTabela(false);
                        })
                      }
                    >
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="16"
                        height="16"
                        fill="currentColor"
                        className="bi bi-pencil-square"
                        viewBox="0 0 16 16"
                      >
                        <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                        <path
                          fill-rule="evenodd"
                          d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"
                        />
                      </svg>
                    </Button>{" "}
                    <Button
                      onClick={prepararFormulario(
                        usuario.usuario_id,
                        usuario.usuario_nome,
                        usuario.usuario_cpf,
                        usuario.usuario_email,
                        usuario.usuario_senha,
                        "exclusao"
                      )}
                    >
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="10"
                        height="16"
                        fill="currentColor"
                        className="bi bi-trash"
                        viewBox="0 0 16 16"
                      >
                        <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5Zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5Zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6Z" />
                        <path d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1ZM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118ZM2.5 3h11V2h-11v1Z" />
                      </svg>{" "}
                    </Button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </Row>
    </Container>
  );
}
