import React, { useState } from "react";

const TabelaReservas = ({ reservas, editarReserva, excluirReserva }) => {
  const [campoOrdenacao, setCampoOrdenacao] = useState("");
  const [ordem, setOrdem] = useState("asc");

  // Função para ordenar as reservas com base no campo selecionado e na ordem
  const ordenarReservas = (campo, ordem) => {
    return reservas.sort((a, b) => {
      if (ordem === "asc") {
        return a[campo] > b[campo] ? 1 : -1;
      } else {
        return a[campo] < b[campo] ? 1 : -1;
      }
    });
  };

  // Função para lidar com a mudança do campo de ordenação
  const handleCampoOrdenacaoChange = (campo) => {
    // Se o campo selecionado for o mesmo que o campo de ordenação atual,
    // muda a ordem; caso contrário, mantém a ordem ascendente
    setCampoOrdenacao(campo);
    setOrdem(campo === campoOrdenacao ? (ordem === "asc" ? "desc" : "asc") : "asc");
  };

  // Ordena as reservas com base no campo e na ordem selecionados
  const reservasOrdenadas = ordenarReservas(campoOrdenacao, ordem);

  return (
    <div className="tabela-reservas">
      <table className="table mx-auto">
        {/* Cabeçalho da tabela */}
        <thead>
          <tr>
            <th scope="col" className="text-center" onClick={() => handleCampoOrdenacaoChange("data")}>
              Data
            </th>
            <th scope="col" className="text-center" onClick={() => handleCampoOrdenacaoChange("horaInicio")}>
              Horário Início
            </th>
            <th scope="col" className="text-center" onClick={() => handleCampoOrdenacaoChange("horaFim")}>
              Horário Fim
            </th>
            <th scope="col" className="text-center" onClick={() => handleCampoOrdenacaoChange("quadra.tipo")}>
              Quadra
            </th>
            <th scope="col" className="text-center" onClick={() => handleCampoOrdenacaoChange("quadra.localizacao")}>
              Localização
            </th>
            <th scope="col" className="text-center" onClick={() => handleCampoOrdenacaoChange("quadra.preco")}>
              Preço
            </th>
            <th scope="col" className="text-center">
              Ações
            </th>
          </tr>
        </thead>
        {/* Corpo da tabela */}
        <tbody>
          {reservasOrdenadas.map((reserva) => (
            <tr key={reserva.id}>
              <td className="text-center">{reserva.data}</td>
              <td className="text-center">{reserva.horaInicio}</td>
              <td className="text-center">{reserva.horaFim}</td>
              <td className="text-center">{reserva.quadra.tipo}</td>
              <td className="text-center">{reserva.quadra.localizacao}</td>
              <td className="text-center">{reserva.quadra.preco}</td>
              <td className="text-center">
                {/* Botões de ação para editar e excluir */}
                <button
                  className="btn btn-primary mr-2"
                  onClick={() => editarReserva(reserva.id)}
                >
                  Editar
                </button>
                <button
                  className="btn btn-danger"
                  onClick={() => excluirReserva(reserva.id)}
                >
                  Excluir
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TabelaReservas;
