import React, { useState } from "react";
import "./modalReserva.css";

const ModalReserva = ({ showModal, closeModal, handleSubmit }) => {
  const [data, setData] = useState("");
  const [horaInicio, setHoraInicio] = useState("");
  const [horaFim, setHoraFim] = useState("");

  const handleDateChange = (e) => {
    setData(e.target.value);
  };

  const handleHoraInicioChange = (e) => {
    setHoraInicio(e.target.value);
  };

  const handleHoraFimChange = (e) => {
    const horaFimSelecionada = e.target.value;
    // Verifica se a hora de fim selecionada é anterior à hora de início
    if (horaInicio && horaFimSelecionada < horaInicio) {
      // Se for, mantém a hora de fim igual à hora de início
      setHoraFim(horaInicio);
    } else {
      // Caso contrário, atualiza a hora de fim normalmente
      setHoraFim(horaFimSelecionada);
    }
  };

  // Gera as opções de hora de 6h às 22h
  const generateHorasOptions = () => {
    const horasOptions = [];
    for (let hora = 6; hora <= 22; hora++) {
      const horaFormatada = `${hora < 10 ? "0" : ""}${hora}:00`;
      horasOptions.push(horaFormatada);
    }
    return horasOptions;
  };

  return (
    showModal && (
      <div className="modal-container">
        <div className="modal-card">
          <div className="card-header">
            <h2 className="card-title">Cadastro de Reserva </h2>
          </div>
          <div className="card-body">
            <form onSubmit={(e) => handleSubmit()}>
              <div className="form-group">
                <label htmlFor="date">Data:</label>
                <input
                  type="date"
                  id="date"
                  min={new Date().toISOString().split("T")[0]} // Define a data mínima como a data de hoje
                  value={data}
                  onChange={handleDateChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="horaInicio">Hora Início:</label>
                <select
                  id="horaInicio"
                  value={horaInicio}
                  onChange={handleHoraInicioChange}
                  required
                >
                  <option value="">Selecione...</option>
                  {generateHorasOptions().map((hora) => (
                    <option key={hora} value={hora}>
                      {hora}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="horaFim">Hora Fim:</label>
                <select
                  id="horaFim"
                  value={horaFim}
                  onChange={handleHoraFimChange}
                  required
                >
                  <option value="">Selecione...</option>
                  {generateHorasOptions().map((hora) => (
                    <option key={hora} value={hora}>
                      {hora}
                    </option>
                  ))}
                </select>
              </div>

              {/* Botão para reservar */}
              <button className="btn btn-primary mb-4" type="submit">
                Reservar
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalReserva;
