// QuadrasList.js

import React, { useEffect, useState } from "react";
import api from "./api";
import authService from "./authService";

const QuadrasList = ({ onChange }) => {
  // Aceita a função onChange como propriedade
  const [quadras, setQuadras] = useState([]);

  useEffect(() => {
    const fetchQuadras = async () => {
      try {
        const token = authService.getToken();
        const response = await api.get("/quadras-info", {
          headers: {
            Authorization: `Bearer ${token}`, // Adicione o token JWT no cabeçalho Authorization
            "Content-Type": "application/json", // Especifique o tipo de conteúdo da solicitação
          },
        });
        setQuadras(response.data);
      } catch (error) {
        console.error("Erro ao obter dados das quadras:", error);
      }
    };

    fetchQuadras();
  }, []);

  const handleQuadraChange = (e) => {
    const selectedQuadraId = e.target.value;
    onChange(selectedQuadraId); // Chama a função onChange com o valor selecionado
  };

  return (
    <div className="form-group">
      <label htmlFor="court">Lista de Quadras Disponíveis</label>
      <select
        id="court"
        onChange={handleQuadraChange}
        required
        disabled={camposDesabilitados}
      >
        {" "}
        {/* Adiciona o evento onChange */}
        <option>Selecione...</option>
        {quadras.map((quadra) => (
          <option key={quadra.id} value={quadra.id}>
            {quadra.tipo}- {quadra.localizacao}
          </option>
        ))}
      </select>
    </div>
  );
};

export default QuadrasList;
