import Pagina from "../templates/Pagina";
import TabelaUsuario from "../Tables/TabelaDeUsuario";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Util/util";

export default function TelaCadastroDeCliente(props) {
  const [status, setStatus] = useState(STATUS.sucesso);
  const [listaCliente, setListaCliente] = useState([]);
  const [atualizando, setAtualizando] = useState(false);
  const [exibeTabela, setExibirTabela] = useState(true);
  const [clienteEmEdicao, setClienteEmEdicao] = useState({
    id: 0,
    name: "",
    cpf: "",
    celular: "",
    email: "",
    senha: "",
  });

  const [excluirCliente, setExcluirCliente] = useState({});
  const [acao, setAcao] = useState("");

  function buscarClientes() {
    fetch("http://localhost:4000/cliente", { method: "GET" })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setlistaReservas(dados);
        setStatus(STATUS.sucesso);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
        console.log(erro.messagem);
      });
  }

  useEffect(() => {
    setStatus(STATUS.ocioso);
    buscarClientes();
  }, []);

  const preparaAcao = (acao) => setAcao(acao);

  const preparaClienteExcluir = (cliente) => setExcluirCliente(cliente);

  const prepararClienteEdicao = (cliente) => {
    setAtualizando(true);
    setClienteEmEdicao(cliente);
  };

  return (
    <Pagina>
      {exibeTabela ? (
        status === STATUS.ocioso ? (
          <TelaCarregamento />
        ) : status === STATUS.sucesso ? (
          <TabelaUsuario
            dados={listaCliente}
            onTabela={setExibirTabela}
            editarUsuario={prepararClienteEdicao}
            editarAtualizando={setAtualizando}
            dadosExcluir={preparaClienteExcluir}
            acaoEditar={setAcao}
            acaoBotao={preparaAcao}
          />
        ) : (
          <TelaErro messagem="Não foi possível recuperar os dados dos clientes" />
        )
      ) : (
        <FormCadCliente
          clientes={clienteEmEdicao}
          exclui={excluirCliente}
          listaCliente={listaCliente}
          atualizando={atualizando}
          onTabela={setExibirTabela}
          botAcao={acao}
        />
      )}
    </Pagina>
  );
}
