import React, { useState } from "react";
import "./reserva.css";

function Reserva() {
  // Estado para controlar a exibição da modal
  const [showModal, setShowModal] = useState(false);

  // Função para abrir a modal
  const openModal = () => {
    setShowModal(true);
  };

  // Função para fechar a modal
  const closeModal = () => {
    setShowModal(false);
  };

  // Função para editar uma reserva
  const editarReserva = (id) => {
    // Lógica para editar a reserva com o ID fornecido
    console.log("Editar reserva com ID:", id);
  };

  // Função para excluir uma reserva
  const excluirReserva = (id) => {
    // Lógica para excluir a reserva com o ID fornecido
    console.log("Excluir reserva com ID:", id);
  };

  return (
    <div className="container mt-5">
      <Menu /> {/* Adicione o componente Menu */}
      <h1 className="mb-4 text-center">Reservas</h1>
      <button className="btn btn-primary mb-4" onClick={openModal}>
        Fazer Reserva
      </button>
      {/* Tabela de reservas */}
      <table className="table mx-auto">
        <thead>
          <tr>
            <th scope="col" className="text-center">
              Data
            </th>
            <th scope="col" className="text-center">
              Horário
            </th>
            <th scope="col" className="text-center">
              Quadra
            </th>
            <th scope="col" className="text-center">
              Ações
            </th>
          </tr>
        </thead>
        <tbody>
          {/* Linhas da tabela com dados de reserva */}
          {/* Você pode mapear os dados das reservas aqui */}
          <tr>
            <td className="text-center">10/03/2024</td>
            <td className="text-center">15:00 - 16:00</td>
            <td className="text-center">Quadra A</td>
            <td className="text-center">
              {/* Botões de ação para editar e excluir */}
              <button
                className="btn btn-primary mr-2"
                onClick={() => editarReserva(1)}
              >
                Editar
              </button>
              <button
                className="btn btn-danger"
                onClick={() => excluirReserva(1)}
              >
                Excluir
              </button>
            </td>
          </tr>
          {/* Adicione mais linhas conforme necessário */}
        </tbody>
      </table>
      {/* Modal para fazer reserva */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h2>Fazer Reserva</h2>
            {/* Conteúdo da modal para fazer reserva */}
          </div>
        </div>
      )}
    </div>
  );
}

export default Reserva;
