import React, { useState } from "react";
import "./modalReserva.css";

const ModalReserva = ({ showModal, closeModal, handleSubmit }) => {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [court, setCourt] = useState("");

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  const handleTimeChange = (e) => {
    setTime(e.target.value);
  };

  const handleCourtChange = (e) => {
    setCourt(e.target.value);
  };

  return (
    showModal && (
      <div className="modal-container">
        <div className="modal-card">
          <div className="card-header">
            <h2 className="card-title">
              Cadastro de Reserva{" "}
              <button className="close-button" onClick={closeModal}>
                Fechar
              </button>
            </h2>
          </div>
          <div className="card-body">
            <form onSubmit={(e) => handleSubmit(e, date, time, court)}>
              <div className="form-group">
                <label htmlFor="date">Data:</label>
                <input
                  type="date"
                  id="date"
                  value={date}
                  onChange={handleDateChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="time">Hora:</label>
                <input
                  type="time"
                  id="time"
                  value={time}
                  onChange={handleTimeChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="court">Selecione a quadra:</label>
                <select
                  id="court"
                  value={court}
                  onChange={handleCourtChange}
                  required
                >
                  <option value="">Selecione...</option>
                  <option value="quadra1">Quadra 1</option>
                  <option value="quadra2">Quadra 2</option>
                  <option value="quadra3">Quadra 3</option>
                </select>
              </div>
              <button type="submit">Reservar</button>
            </form>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalReserva;
