import React, { createContext, useState } from "react";
import {  Navigate } from "react-router-dom";

import api from "../pages/api";

const Context = createContext();

function AuthProvider({ children }) {
  const [authenticated, setAuthenticated] = useState(false);

  async function handleLogin(email, senha) {
    try {
      console.log(email, senha);
      const response = await api.post("/login", {
        email,
        senha,
      });

      console.log(response.data); // Aqui você pode tratar a resposta do backend
      setAuthenticated(true); // Define o estado de autenticação como verdadeiro
    } catch (error) {
      console.error("Erro ao fazer login:", error);
      // Aqui você pode tratar o erro de acordo com sua lógica de frontend
    }
  }

  return (
    <Context.Provider value={{ authenticated, handleLogin }}>
      {children}
      {authenticated && <Navigate to="/home" replace />}
    </Context.Provider>
  );
}

export { Context, AuthProvider };
