import React, { useRef, useState } from "react";
import { Container } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Row from "react-bootstrap/Row";
import STATUS from "../Util/util";
import TelaCarregamento from "../Screen/TelaCarregamento";
import TelaErro from "../Screen/TelaErro";

export default function FormCadUsuario(props) {
  function cadastrarUsuario(usuario) {
    fetch("http://localhost:4000/usuario", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(usuario),
    })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        alert(dados.mensagem);
        setStatus(STATUS.sucesso);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
      });
  }

  const [formValidado, setFormValidado] = useState(false);
  const [status, setStatus] = useState(STATUS.sucesso);

  const id = useRef("");
  const nome = useRef("");
  const cpf = useRef("");
  const email = useRef("");
  const senha = useRef("");

  function validarDados() {
    const usuario = {
      id: id.curret.value,
      nome: nome.curret.value,
      cpf: cpf.curret.value,
      email: email.curret.value,
      senha: senha.curret.value,
    };

    if (
      usuario.id &&
      usuario.nome &&
      usuario.cpf &&
      usuario.email &&
      usuario.senha
    )
      return usuario;
    else return undefined;
  }

  function manipularSubmissao(evento) {
    const formulario = evento.currentTarget;
    if (formulario.checkValidity()) {
      const usuario = validarDados();
      if (usuario) {
        setStatus(STATUS.ocioso);
        cadastrarUsuario(usuario);
      }
    }
    evento.preventDefault();
    evento.stopPropagation();
    setFormValidado(true);
  }

  if (status === STATUS.sucesso) {
    return (
      <Container>
        <Row className="mt-3 mb-3 border d-flex text-center">
          <h2>Formulario de Cadastro de Usuario</h2>
        </Row>
        <Row className="mt-3 p-2 border">
          <Form
            noValidate
            validated={formValidado}
            onSubmit={manipularSubmissao}
          >
            <Row className="mb-3">
              <Form.Group as={Col} md="3">
                <Form.Label>Nome</Form.Label>
                <Form.Control
                  id="nome"
                  name="nome"
                  required
                  type="text"
                  placeholder="Nome"
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o nome do Usuario
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>C.P.F</Form.Label>
                <Form.Control
                  id="cpf"
                  name="cpf"
                  required
                  type="text"
                  placeholder="C.P.F"
                />
                <Form.Control.Feedback type="invalid">
                  Por favor, Informa o C.P.F do Usuario
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>Email</Form.Label>
                <InputGroup hasValidation>
                  <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                  <Form.Control
                    id="email"
                    name="email"
                    type="email"
                    placeholder="E-mail"
                    aria-describedby="inputGroupPrepend"
                    required
                  />
                  <Form.Control.Feedback type="invalid">
                    Por favor, Informa o E-mail do Usuario
                  </Form.Control.Feedback>
                </InputGroup>
              </Form.Group>
              <Form.Group as={Col} md="3">
                <Form.Label>Senha</Form.Label>
                <InputGroup hasValidation>
                  <Form.Control
                    id="senha"
                    name="senha"
                    type="password"
                    placeholder="Senha"
                    required
                  />
                  <Form.Control.Feedback type="invalid">
                    Por favor, Informa a Senha do Usuario
                  </Form.Control.Feedback>
                </InputGroup>
              </Form.Group>
            </Row>
            <Button type="submit" variant="success">
              Cadastrar
            </Button>{" "}
            <Button type="submit" variant="danger">
              Limpar
            </Button>{" "}
            <Button
              onClick={() => {
                props.onTabela(true);
              }}
              type="submit"
              variant="outline-primary"
            >
              Voltar
            </Button>
          </Form>
        </Row>
      </Container>
    );
  } else if (status === STATUS.ocioso) {
    return <TelaCarregamento />;
  } else {
    return <TelaErro mensagem="Não foi possivel Gravar o Cliente" />;
  }
}
