import React, { useState } from "react";
import "./modalReserva.css";
import QuadrasList from "./quadraList";
import api from "./api";
import authService from "./authService";

const ModalReserva = ({ showModal, closeModal, handleSubmit }) => {
  const [data, setData] = useState("");
  const [horaInicio, setHoraInicio] = useState(""); // Remova o espaço em branco
  const [horaFim, setHoraFim] = useState(""); // Remova o espaço em branco
  const [quadraId, setQuadraId] = useState("");
  const [disponibilidade, setDisponibilidade] = useState(false);

  const handleDateChange = (e) => {
    setData(e.target.value);
  };

  const handleHoraInicioChange = (e) => {
    setHoraInicio(e.target.value);
  };

  const handleHoraFimChange = (e) => {
    setHoraFim(e.target.value);
  };

  const handleQuadraChange = (e) => {
    setQuadraId(e.target.value);
  };

  const handleVerificarDisponibilidade = async () => {
    console.log(data, horaInicio, horaFim, quadraId);
    /*try {
      const token = authService.getToken();
      const response = await api.get(
        "reservadisponivel",
        { data, horaInicio, horaFim, quadraId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      // Implemente a lógica para verificar a disponibilidade da reserva
      // Atualize o estado de disponibilidade com o resultado da verificação
      setDisponibilidade(true); // Supondo que a reserva está disponível
    } catch (error) {
      console.error("Erro ao verificar disponibilidade:", error);
    }*/
  };

  return (
    showModal && (
      <div className="modal-container">
        <div className="modal-card">
          <div className="card-header">
            <h2 className="card-title">Cadastro de Reserva</h2>
          </div>
          <div className="card-body">
            <form onSubmit={(e) => handleSubmit()}>
              <QuadrasList onChange={handleQuadraChange} />
              <div className="form-group">
                <label htmlFor="data">Data:</label>
                <input
                  type="date"
                  id="data"
                  value={data}
                  onChange={handleDateChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="horaInicio">Hora Inicio:</label>
                <input
                  type="time"
                  id="horaInicio"
                  value={horaInicio}
                  onChange={handleHoraInicioChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="horaFim">Hora Fim:</label>
                <input
                  type="time"
                  id="horaFim"
                  value={horaFim}
                  onChange={handleHoraFimChange}
                  required
                />
              </div>

              <button
                className="btn btn-secondary mb-4 mr-2"
                onClick={handleVerificarDisponibilidade}
              >
                Verificar Disponibilidade
              </button>

              <button
                className="btn btn-primary mb-4"
                type="submit"
                disabled={!disponibilidade}
              >
                Reservar
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  );
};

export default ModalReserva;
