import Pagina from "../templates/Pagina";
import FormCadUsuario from "../Forms/FormCadUsuario";
import TabelaUsuario from "../Tables/TabelaDeUsuario";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Util/util";

export default function TelaCadastroDeUsuario(props) {
  const [exibeTabela, setExibirTabela] = useState(true);
  const [status, setStatus] = useState(STATUS.sucesso);
  const [usuarios, setUsuarios] = useState({
    id: 0,
    name: "",
    cpf: "",
    email: "",
    senha: "",
  });
  const [atualizando, setAtualizando] = useState(false);
  const [prepararUsuarioEdicao, setPrepararUsuarioEdicao] =
    function prepararUsuarioEdicao(usuarios) {
      setAtualizando(true);
      exibeTabela(false);
    };

  function buscarUsuario() {
    fetch("http://localhost:4000/usuario", { method: "GET" })
      .then((res) => {
        return res.json();
      })
      .then((dados) => {
        setUsuarios(dados);
        setStatus(STATUS.sucesso);
      })
      .catch((erro) => {
        setStatus(STATUS.erro);
        console.log(erro.mensagem);
      });
  }

  useEffect(() => {
    setStatus(STATUS.ocioso);
    buscarUsuario();
  }, []);

  if (exibeTabela) {
    if (status === STATUS.ocioso) {
      return <TelaCarregamento />;
    } else if (status === STATUS.sucesso) {
      return (
        <Pagina>
          <TabelaUsuario dados={usuarios} onTabela={setExibirTabela} />
        </Pagina>
      );
    } else {
      return (
        <TelaErro mensagem="Não foi possivel recuperar os dados dos Usuarios"></TelaErro>
      );
    }
  } else {
    return (
      <Pagina>
        <FormCadUsuario
          usuarios={usuarios}
          atualizando={atualizando}
          onTabela={setExibirTabela}
        />
      </Pagina>
    );
  }
}
